/* Units details -- meta data of all the units*/
CREATE TABLE UNITS_DETAILS (
	UNIT_ID VARCHAR(5) UNIQUE,		-- unique identifier of each unit
	UNIT_TRIBE VARCHAR (10),		-- Race of the unit
	TRIBE_ID INT(1), 				-- Race identifier 
	UNIT_NAME VARCHAR(25),			-- name of the unit
	UNIT_TYPE INT(1),				-- Type of unit (1=offense, 2=defense, 3=hybrid, 0=none)
	UNIT_UPKEEP INT(2),				-- upkeep of the unit 
	UNIT_CARRY INT(5),				-- Unit carry capacity
	UNIT_SPEED INT(3),				-- speed of the unit
	UNIT_OFFENSE FLOAT(10),			-- Offense value at 0
	UNIT_OFFENSE_MAX FLOAT(10)	,		-- offense value at 20
	UNIT_INF_DEFENSE FLOAT (10),		-- Infantry defense at 0	
	UNIT_INF_DEFENSE_MAX FLOAT (10),	-- Infantry defense at 20 
	UNIT_CAV_DEFENSE FLOAT (10),		-- Cavalry defense at 0
	UNIT_CAV_DEFENSE_MAX FLOAT (10),	-- Cavalry defense at 20
	UNIT_COST INT(6),				-- Total cost of unit
	UNIT_COST_WOOD INT(5),			-- cost of wood for each unit
	UNIT_COST_CLAY INT(5),			-- cost of clay for each unit
	UNIT_COST_IRON INT(5),			-- cost of iron for each unit
	UNIT_COST_CROP INT(5),			-- cost of crop for each unit
	UNIT_BUILD_TIME INT(6),			-- Unit build time in seconds	
	UNIT_ICON VARCHAR(55),			-- Icon file name for the unit
	UNIT_IMAGE VARCHAR(55)			-- Image file name for the unit
);

